// src/pages/CreateAnecdote.js
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function CreateAnecdote() {
  const [content, setContent] = useState("");
  const [title, setTitle] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post("http://127.0.0.1:8000/api/anecdotes", {
      title,
      content,
    }, { withCredentials: true })
    .then(() => {
      navigate("/"); // rediriger vers page d’accueil
    })
    .catch(err => {
      console.error("Erreur création :", err);
    });
  };

  return (
    <div className="min-h-screen bg-black text-white p-8">
      <h1 className="text-3xl font-bold mb-6">Partager une anecdote</h1>
      <form onSubmit={handleSubmit} className="space-y-4 max-w-xl">
        <input
          type="text"
          placeholder="Titre"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="w-full p-3 rounded bg-gray-800 text-white border border-gray-700"
          required
        /> <br/>
        <textarea
          placeholder="Votre anecdote..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
          className="w-full p-3 rounded bg-gray-800 text-white border border-gray-700 h-40"
          required
        ></textarea>
        <button
          type="submit"
          className="bg-yellow-400 hover:bg-yellow-300 text-black font-semibold py-2 px-6 rounded"
        >
          Publier
        </button>
      </form>
    </div>
  );
}
