// src/components/Register.js
import { useState } from "react";
import api, { csrf } from "../api";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const [form, setForm] = useState({ name: "", email: "", password: "", password_confirmation: "" });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await csrf(); // Obligatoire pour Sanctum
      await api.post("/register", form);
      navigate("/"); // Redirection après inscription
    }catch (err) {
  console.error("Erreur complète:", err); // Ajoute ceci pour tout voir dans la console
  if (err.response) {
    setError(JSON.stringify(err.response.data.errors || err.response.data.message));
  } else {
    setError("Erreur réseau ou serveur injoignable");
  }
}
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Inscription</h2>
      <input name="name" placeholder="Nom" onChange={handleChange} required /> <br/>
      <input name="email" type="email" placeholder="Email" onChange={handleChange} required /> <br/>
      <input name="password" type="password" placeholder="Mot de passe" onChange={handleChange} required /> <br/>
      <input name="password_confirmation" type="password" placeholder="Confirmation" onChange={handleChange} required />
      {error && <p style={{ color: "red" }}>{error}</p>}
      <button type="submit">S'inscrire</button>
    </form>
  );
}
